import{A as e}from"./chunks/AppRelease.Bodbp7ZV.js";import{c as a,J as t,a5 as r,o as s}from"./chunks/framework.-i2sbza6.js";const l=r(`<h1 id="install" tabindex="-1">Install <a class="header-anchor" href="#install" aria-label="Permalink to &quot;Install&quot;">​</a></h1><h3 id="system-requirement" tabindex="-1">System Requirement <a class="header-anchor" href="#system-requirement" aria-label="Permalink to &quot;System Requirement&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">Platform</th><th style="text-align:left;">Minimum Version</th></tr></thead><tbody><tr><td style="text-align:left;">Android</td><td style="text-align:left;">Android 6.0 (API level 23)</td></tr><tr><td style="text-align:left;">iOS</td><td style="text-align:left;">iOS 12.0</td></tr><tr><td style="text-align:left;">Windows</td><td style="text-align:left;">Windows 10, x64</td></tr><tr><td style="text-align:left;">macOS</td><td style="text-align:left;">macOS 10.14</td></tr><tr><td style="text-align:left;">Linux</td><td style="text-align:left;">Debian 10, x64 <sup>1</sup></td></tr><tr><td style="text-align:left;">Web<sup>2</sup></td><td style="text-align:left;"><a href="https://chaldea.center" target="_blank" rel="noreferrer">https://chaldea.center</a></td></tr><tr><td style="text-align:left;">Web(China)</td><td style="text-align:left;"><a href="https://cn.chaldea.center" target="_blank" rel="noreferrer">https://cn.chaldea.center</a></td></tr></tbody></table><blockquote><p><sup>1</sup> Only <strong>Debian</strong> based Linux distributions are supported and tested. While it may work on other Linux distributions, we cannot guarantee full compatibility nor offer technical support for issues arising from those platforms at this time.</p><p><sup>2</sup> <strong>Mobile browser</strong> is not recommended due to bad performance and compatibility. Starting from 2.5.0, newer browser version is required, see <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/WeakRef/WeakRef#browser_compatibility" target="_blank" rel="noreferrer">WeakRef</a> and <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/globalThis#browser_compatibility" target="_blank" rel="noreferrer">globalThis</a></p></blockquote><p>Check <a href="https://docs.flutter.dev/reference/supported-platforms" target="_blank" rel="noreferrer">Flutter docs</a> for more about supported platforms.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Don&#39;t run multiple instances or web pages together, otherwise data will be overridden by each other.</p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>If you encounter installation/startup problems, please check <a href="#notes">Notes</a> first.</p></div><h2 id="download" tabindex="-1">Download <a class="header-anchor" href="#download" aria-label="Permalink to &quot;Download&quot;">​</a></h2><h3 id="google-play" tabindex="-1">Google Play <a class="header-anchor" href="#google-play" aria-label="Permalink to &quot;Google Play&quot;">​</a></h3><p><a href="https://play.google.com/store/apps/details?id=cc.narumi.chaldea" target="_blank" rel="noreferrer"><img alt="Get it on Google Play" src="https://play.google.com/intl/en_us/badges/static/images/badges/en_badge_web_generic.png" width="137.5"></a><a href="https://f-droid.org/packages/cc.narumi.chaldea.fdroid/" target="_blank" rel="noreferrer"><img alt="Get it on F-droid" src="https://fdroid.gitlab.io/artwork/badge/get-it-on.png" width="137.5"></a></p><p><a href="https://play.google.com/store/apps/details?id=cc.narumi.chaldea" target="_blank" rel="noreferrer">Get it on Google Play</a></p><h3 id="app-store" tabindex="-1">App Store <a class="header-anchor" href="#app-store" aria-label="Permalink to &quot;App Store&quot;">​</a></h3><p><a href="https://apps.apple.com/us/app/chaldea/id1548713491?itsct=apps_box&amp;itscg=30200" target="_blank" rel="noreferrer"><img src="https://tools.applemediaservices.com/api/badges/download-on-the-app-store/black/en-US?size=250x83&amp;releaseDate=1610841600&amp;h=cb0adac232fdd6b88894f78b2f349b6e" alt="Download on the App Store" width="120"></a></p><p><a href="https://apps.apple.com/us/app/chaldea/id1548713491?itsct=apps_box&amp;itscg=30200" target="_blank" rel="noreferrer">Download on the App Store</a></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Including iOS App Store and Mac App Store</p></div><h3 id="github-release" tabindex="-1">GitHub Release <a class="header-anchor" href="#github-release" aria-label="Permalink to &quot;GitHub Release&quot;">​</a></h3><p>Contains Android, Windows and Linux binary assets.</p><ul><li><a href="https://github.com/chaldea-center/chaldea/releases" target="_blank" rel="noreferrer">Github Releases</a></li><li><a href="#releases">Releases</a></li></ul><p>If the latest version doesn&#39;t contain the platform you want, please check the previous versions.</p><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><h3 id="android" tabindex="-1">Android <a class="header-anchor" href="#android" aria-label="Permalink to &quot;Android&quot;">​</a></h3><p>All android ABIs(arm64-v8a, armeabi-v7a, x86_64) are merged into one apk in v2.x.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>If you are using v1.x, you have to UNINSTALL old version first! All data will be swiped, so make sure to backup to local before upgrading. V1 server backup is incompatible with v2.</p></div><h3 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Please run the app after decompression. And save to non-system folder, e.g. <code>Program File</code> is not recommended, you may need admin permission, and some functions may not work properly.</p></div><p><strong>Error: <code>VCRUNTIME140_1.dll was not found</code></strong></p><p>If there is such an error message on startup, please install <a href="https://support.microsoft.com/en-us/help/2977003/the-latest-supported-visual-c-downloads" target="_blank" rel="noreferrer">Microsoft Visual C++ redistributable package</a> and restart again.</p><h3 id="macos" tabindex="-1">macOS <a class="header-anchor" href="#macos" aria-label="Permalink to &quot;macOS&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The macOS version is only available on Mac App Store.</p></div><h3 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h3><p>If app cannot be started, try to run it from terminal <code>./chaldea</code> and check the possible error.</p><p>Since v2.3.0, desktop app can be shown in system tray, and <code>libappindicator</code> is required in linux system. If it doesn&#39;t exist in your system, please install it before launching chaldea app, otherwise startup will fail silently.</p><p>Due to some errors, the feature is disabled but the lib is still required.</p><p>For Debian based Linux, you can install it through the following command:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># Debian based</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt install libappindicator3-1</span></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># Ubuntu 22.04 or higher</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt-get install libayatana-appindicator3-dev</span></span></code></pre></div><h2 id="releases" tabindex="-1">Releases <a class="header-anchor" href="#releases" aria-label="Permalink to &quot;Releases&quot;">​</a></h2><blockquote><p>For FGO game apk, please visit <a href="./fgo_apk">FGO APK</a>.</p></blockquote><p>This section lists recent <strong>Chaldea</strong> stable and beta download links for Android, Linux and Windows.</p><p>If you have trouble downloading from the <strong>Github</strong> link, please use the <strong>Proxy</strong> link.</p><p>More releases can be found at <a href="https://github.com/chaldea-center/chaldea/releases" target="_blank" rel="noreferrer">Github Releases</a></p><p>Tips: <code>beta</code> version is the latest preview version, updated when source code got updated.</p><p>To download the latest stable version:</p><ul><li>Android: <a href="https://worker.chaldea.center/releases/latest/android" target="_blank" rel="noreferrer">https://worker.chaldea.center/releases/latest/android</a></li><li>Windows: <a href="https://worker.chaldea.center/releases/latest/windows" target="_blank" rel="noreferrer">https://worker.chaldea.center/releases/latest/windows</a></li><li>Linux: <a href="https://worker.chaldea.center/releases/latest/linux" target="_blank" rel="noreferrer">https://worker.chaldea.center/releases/latest/linux</a></li></ul>`,43),h=JSON.parse('{"title":"Install","description":"","frontmatter":{},"headers":[],"relativePath":"guide/install.md","filePath":"guide/install.md","lastUpdated":1708291033000}'),o={name:"guide/install.md"},u=Object.assign(o,{setup(i){return(n,d)=>(s(),a("div",null,[l,t(e)]))}});export{h as __pageData,u as default};
